mapper.select:
select count(1) from role

    and deleted=0
    and id = #{arg0}

mapper.update:
update role set
    `code`=#{code,jdbcType=VARCHAR},
    role_name=#{roleName,jdbcType=VARCHAR},
    operated_time=#{operatedTime,jdbcType=TIMESTAMP},
    operated_by=#{operatedBy,jdbcType=VARCHAR},
    version = version+1,
    deleted=#{deleted,jdbcType=BOOLEAN}
where id=#{id,jdbcType=BIGINT}
and version=#{version,jdbcType=INTEGER}
and deleted=0

mapper.delete:
update role set deleted=1
where id=#{arg0,jdbcType=BIGINT}
and deleted=0

mapper.select:
select
    t.id as `key`,
    t.role_name as `value`
from role t

    and t.deleted=0

        and t.id > #{lastKey}



        and t.role_name like #{matchValue_pattern}


order by t.id asc
limit #{limit}

mapper.delete:
delete from r_role_resource
where role_id=#{roleId,jdbcType=BIGINT} and resource_id in

    #{_id}
